INSERT INTO `portal_experts` VALUES (1, '朱瑾', '个人说明： 教育学学士，副教授，现任职于常州开放大学图书馆馆长以及学校心理服务中心负责人。国家高级婚姻家庭咨询师。擅长儿童心理辅导、家庭教育辅导、家庭教育演讲。亲子沟通、情绪、职业规划类等问题的咨询辅导。', '/storage/8gsupPXx5nsNFe8KwNhGjDGFu6JmZD9xirN3D6Jf.png', '心理咨询机构', '国家二级心理咨询师', '0610000008200491', NULL, NULL);
INSERT INTO `portal_experts` VALUES (2, '张冬梅', '个人说明：江苏理工学院心理学教授。江苏理工学院心理教育研究所副所长。擅长讲座、团体辅导、青少年心理咨询、中小学生学习、人际、情感、亲子关系类等问题的咨询辅导。', '/storage/wbTc69uootPjuWwexlAQ5mIoKXtNVUYSUSHgIQ2v.jpeg', '心理咨询机构', '国家二级心理咨询师', '0510000008200255', NULL, NULL);
INSERT INTO `portal_experts` VALUES (3, '赵世俊', '个人说明：常州市中小学生涯教育名教师工作室领衔人，江苏省常州高级中学学生成长指导中心负责人。擅长家庭教育辅导、生涯规划辅导、青少年心理训练、心理障碍矫治。', '/storage/j9ngcM3rtt2sFzBx9L7jShPkOyd1kIg9t1DLfdJO.jpeg', '心理咨询', '国家二级心理咨询师', '0610000008200490', NULL, NULL);
INSERT INTO `portal_experts` VALUES (4, '崔景贵', '个人说明：江苏理工学院副院长、心理教育研究所所长、江苏省职业技术教育科学研究中心主任，教授（二级），博士研究生，教育学博士后，硕士生导师。擅长青少年心理成长、家庭教育类问题的咨询辅导 擅长解决问题：学前教育心理、家庭教育及幼儿感觉统合训练（电话咨询、讲座、一对一家长咨询）', '/storage/RZbLc4GS8ILiqpccg9QrjsgGlNz7UNMKeRnAPsXa.jpeg', '心理咨询机构', '毕业于中国科学院心理研究所', '小教高级', NULL, NULL);
INSERT INTO `portal_experts` VALUES (5, '方翰青', '个人说明：江苏理工学院教育学院心理学系书记，教授，硕士研究生。擅长演讲、学生学习心理、情绪调试类等问题的咨询辅导。', '/storage/6YzOsNqNGXSuEKrsaxpIyUFeXX1uYyBmbh4bNNEV.png', '心理咨询', '国家二级心理咨询师', '1210000008200388', NULL, NULL);

