DROP TABLE IF EXISTS `portal_reserves`;
CREATE TABLE `portal_reserves`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `realname` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '真实姓名',
  `gender` int(10) UNSIGNED NOT NULL COMMENT '性别：1男 2女',
  `birth` date NOT NULL COMMENT '出生年月',
  `school` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '所在学校',
  `grade` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '年级',
  `mobile` varchar(11) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '手机号',
  `reserve_date` date NOT NULL COMMENT '预约日期',
  `appeal` varchar(800) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '咨询诉求',
  `is_consulted` tinyint(3) UNSIGNED NOT NULL COMMENT '是否做过心理咨询',
  `consulting_desc` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '心理咨询情况说明',
  `is_medical` tinyint(3) UNSIGNED NOT NULL COMMENT '是否就医',
  `medical_desc` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '是否就医情况说明',
  `has_gene` tinyint(3) UNSIGNED NOT NULL COMMENT '家族或亲戚中是否有精神病患或自杀者',
  `gene_desc` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '精神病患或自杀者情况说明',
  `has_violent` tinyint(3) UNSIGNED NOT NULL COMMENT '是否有自杀/自伤或者伤害他人的想法或行为',
  `violent_desc` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '有自杀/自伤或者伤害他人的想法或行为的情况说明',
  `user_id` int(10) UNSIGNED NOT NULL COMMENT '用户id',
  `status` tinyint(4) NOT NULL DEFAULT 0 COMMENT '状态： -1拒绝 0待受理 1通过并受理 2等待首访 3首访中 4面询中 5完成',
  `deleted_at` timestamp(0) NULL DEFAULT NULL,
  `created_at` timestamp(0) NULL DEFAULT NULL,
  `updated_at` timestamp(0) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 2 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = Dynamic;

