-- ----------------------------
-- Table structure for portal_teachers
-- ----------------------------
DROP TABLE IF EXISTS `portal_teachers`;
CREATE TABLE `portal_teachers`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `realname` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '老师姓名',
  `introduction` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '老师简介',
  `photo` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '老师照片',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 8 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of portal_teachers
-- ----------------------------
INSERT INTO `portal_teachers` VALUES (1, '朱瑾', '教育学学士，副教授，现任职于常州开放大学图书馆馆长以及学校心理服务中心负责人。国家高级婚姻家庭咨询师。擅长儿童心理辅导、家庭教育辅导、家庭教育演讲。亲子沟通、情绪、职业规划类等问题的咨询辅导。', '/storage/tDJdN65AqfVnIuTWmqNpZgZDsZd72R1ku090Z6BF.jpeg');
INSERT INTO `portal_teachers` VALUES (2, '方翰青', '江苏理工学院教育学院心理学系书记，教授，硕士研究生。擅长演讲、学生学习心理、情绪调试类等问题的咨询辅导。', '/storage/FjFUOvViNHeQJnKYG3gyrgeK2dRSBpNaj3pRL16H.png');
INSERT INTO `portal_teachers` VALUES (3, '张冬梅', '江苏理工学院心理学教授。江苏理工学院心理教育研究所副所长。擅长讲座、团体辅导、青少年心理咨询、中小学生学习、人际、情感、亲子关系类等问题的咨询辅导。', '/storage/Cs0D9qCnMoxLSfgcEfZzg6CZd7vGayTUgrb1LVhX.jpeg');
INSERT INTO `portal_teachers` VALUES (4, '赵世俊', '常州市中小学生涯教育名教师工作室领衔人，江苏省常州高级中学学生成长指导中心负责人。擅长家庭教育辅导、生涯规划辅导、青少年心理训练、心理障碍矫治。', '/storage/k6bIk8SLpPVRLAHjozs9UhS9F5rsc4ggqo9BAumY.jpeg');
INSERT INTO `portal_teachers` VALUES (5, '崔景贵', '江苏理工学院副院长、心理教育研究所所长、江苏省职业技术教育科学研究中心主任，教授（二级），博士研究生，教育学博士后，硕士生导师。擅长青少年心理成长、家庭教育类问题的咨询辅导 擅长解决问题：学前教育心理、家庭教育及幼儿感觉统合训练（电话咨询、讲座、一对一家长咨询）', '/storage/SOoD1zzKLsyylZ89L5av90YMG9evIlhry3ow7q8b.jpeg');
INSERT INTO `portal_teachers` VALUES (6, '徐欢', '擅长：中学生的学习指导和心理调试；小学生的习惯培养和亲子沟通等。', '/storage/f6YAuAY7gIrDB1p3JsK0CXSVKf9kK8DShASrUz5V.jpeg');
INSERT INTO `portal_teachers` VALUES (7, '胡娜', '围绕“情商”主题，研究少儿情商具体内容，打造设计适合6-12岁少年儿童情商训练课程体系，研究少儿情商的具体内容及测量方法，并开展少儿情商主题公益系列活动。', '/storage/CqwBdFGLsQv8PO3kGD6hDyi4aRTD6wm6Uiy5vWAu.jpeg');

-- ----------------------------
-- Table structure for portal_users
-- ----------------------------
DROP TABLE IF EXISTS `portal_users`;
CREATE TABLE `portal_users`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `username` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '用户名',
  `password` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '密码',
  `openid` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '微信openid',
  `unionid` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `role_id` int(10) UNSIGNED NOT NULL COMMENT '角色id',
  `cover` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '头像',
  `mobile` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '手机号',
  `gender` tinyint(3) UNSIGNED NOT NULL DEFAULT 0 COMMENT '性别:1男,2女,0保密',
  `remember_token` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `deleted_at` timestamp(0) NULL DEFAULT NULL,
  `created_at` timestamp(0) NULL DEFAULT NULL,
  `updated_at` timestamp(0) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 12 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = Dynamic;

